package Assingment;

public class Stringbreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String lang="Selenium";
		
		switch (lang)
		{
		case "Java":
		System.out.println("use java");
		break;
			
		case "Javascript":
		System.out.println("use Javascript");
		break;
		
		case "Selenium":
		System.out.println("use Selenium");
		break;
			
		case "Python":
		System.out.println("use Python");
		break;
			
		case "Mukesh":
		System.out.println("Mukesh");
		break;

		default:
			System.out.println("Use from the list");
			break;
		}
	}

}
